import React from "react";

const Board = ({ board }) => {
  return (
    <div className="bg-white shadow rounded p-4 w-1/3">
      <h2 className="text-xl font-bold mb-4">{board.title}</h2>
      <div className="space-y-2">
        {board.tasks.map((task) => (
          <div
            key={task.id}
            className="p-2 bg-gray-200 rounded shadow-sm text-sm"
          >
            {task.title}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Board;
